<template>
  <el-scrollbar wrap-class="scrollbar-wrapper">
    <el-menu
      :show-timeout="200"
      :default-active="$route.path"
      :collapse="isCollapse"
      :background-color="variables.menuBg"
      :text-color="variables.menuText"
      :active-text-color="variables.menuActiveText"
      mode="vertical"
    >
      <div style='color: red;width: 80px;height: 50px;margin: auto' v-if='!isCollapse'>
        <img  title='钉小保' alt='钉小保' src='data:image/png;base64,' style='width: 80px;margin:17px auto 0'>
      </div>
      <div style='color: red;width:100%;height:50px;margin: auto' v-if='isCollapse'>
        <img title='钉小保' alt='钉小保' src='data:image/png;base64,' style='width: 22px;height: 22px;margin:13px 5px;display:inline-block'>
      </div>

      <sidebar-item v-for="route in routes" :key="route.path" :item="route" :base-path="route.path"/>
    </el-menu>

  </el-scrollbar>
</template>

<script>
import { mapGetters } from 'vuex'
import variables from '@/styles/variables.scss'
import SidebarItem from './SidebarItem'

export default {
  components: { SidebarItem },
  computed: {
    ...mapGetters([
      'sidebar'
    ]),
    routes() {
      return this.$router.options.routes
    },
    variables() {
      return variables
    },
    isCollapse() {
      return !this.sidebar.opened
    }
  }
}
</script>

